function getDataApi() {
fetch("http://hp-api.herokuapp.com/api/characters/house/gryffindor")
.then(response => response.json())
.then(data => {

const dataWeNeed = data.result.map(result => {
 return {

    name:result.name,
    species:result.species,
    }

    });
    return dataWeNeed ;
});
}


export default getDataApi;
