function getApiData() {
return fetch("http://hp-api.herokuapp.com/api/characters")
    .then((response) => response.json())
    .then((data) => {

const ApiDataNeeded = data.map((item, index) => {
const getImage = item.image;
if (getImage === null)
return (
<img scr="text.png" alt="character image"></img>
);

    return { 
        id: index,
        image: item.image,    
        name: item.name,
        species: item.species,
        house: item.house,
        gender: item.gender,
        status: item.status,
    } 

});
return ApiDataNeeded;

});
};
export default getApiData;
